package it.polimi.ingsw.pc22.effects;import it.polimi.ingsw.pc22.gamebox.GameBoard;import it.polimi.ingsw.pc22.player.Player;import java.io.Serializable;/** * This class represents the interface Effect. * It includes the "isLegal" method that will check if the player * that has activated the effect can effectively execute the  * effect with the actual Gameboard's status. * The method "executeEffects" will then apply the effect * to the specified player. * This interface is implemented by all the effects in game, including  * tower/excommunication/leader cards effects. */public interface Effect extends Serializable{	boolean isLegal(Player player, GameBoard gameBoard);	boolean executeEffects(Player player, GameBoard gameBoard);}